alias ping=" docker-compose run kubespray /bin/bash -c 'ansible all -m ping -i inventory/hosts.yml -v'"
alias install="docker-compose run kubespray /bin/bash -c 'ansible-playbook cluster.yml -i inventory/hosts.yml --become' | tee play_log_$( date +"%Y_%m_%d_%H%M%S" ).txt"
alias upgrade="docker-compose run kubespray /bin/bash -c 'ansible-playbook upgrade-cluster.yml -i inventory/hosts.yml --become' | tee upgrade_log_$( date +"%Y_%m_%d_%H%M%S" ).txt"